import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header 
      className={`fixed top-0 left-0 w-full z-50 transition-all duration-500 ${
        scrolled 
          ? 'bg-white/5 backdrop-blur-xl border-b border-white/10' 
          : 'bg-white/3 backdrop-blur-lg'
      }`}
      style={{
        boxShadow: scrolled 
          ? '0 8px 32px rgba(255, 255, 255, 0.1)' 
          : '0 4px 20px rgba(255, 255, 255, 0.05)'
      }}
    >
      <nav className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        {/* Logo with cosmic glow */}
        <div className="relative">
          <h1 
            className="text-3xl font-bold tracking-wider text-transparent bg-gradient-to-r from-blue-300 via-purple-300 to-cyan-300 bg-clip-text filter drop-shadow-lg"
            style={{
              fontFamily: 'Orbitron, monospace',
              textShadow: '0 0 20px rgba(147, 197, 253, 0.5)'
            }}
          >

          </h1>
          {/* Subtle glow effect */}
          <div className="absolute -inset-2 bg-gradient-to-r from-blue-500/20 via-purple-500/20 to-cyan-500/20 blur-xl opacity-50 rounded-full"></div>
        </div>

        {/* Navigation Links */}
        <ul className="flex space-x-8 font-medium text-sm" style={{ fontFamily: 'Orbitron, monospace' }}>
          {['Home', 'About', 'Projects', 'Skills', 'Contact'].map((item, index) => (
            <li key={item} className="relative group">
              <a 
                href={`#${item.toLowerCase()}`}
                className="relative z-10 text-white/90 hover:text-white transition-all duration-300 py-2 px-4 rounded-full hover:bg-white/10 backdrop-blur-sm border border-transparent hover:border-white/20"
                style={{
                  textShadow: '0 0 10px rgba(255, 255, 255, 0.3)'
                }}
              >
                {item}
              </a>

              {/* Hover glow effect */}
              <div className="absolute inset-0 bg-gradient-to-r from-blue-500/20 via-purple-500/20 to-cyan-500/20 rounded-full opacity-0 group-hover:opacity-100 transition-opacity duration-300 blur-sm"></div>

              {/* Animated underline */}
              <div className="absolute bottom-0 left-1/2 w-0 h-0.5 bg-gradient-to-r from-blue-400 to-cyan-400 group-hover:w-full group-hover:left-0 transition-all duration-300 rounded-full"></div>
            </li>
          ))}
        </ul>

        {/* Mobile menu button (optional) */}
        <div className="md:hidden">
          <button className="text-white/80 hover:text-white p-2 rounded-lg bg-white/10 backdrop-blur-sm border border-white/20 hover:bg-white/20 transition-all duration-300">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </nav>

      {/* Subtle top border glow */}
      <div className="absolute top-0 left-0 w-full h-px bg-gradient-to-r from-transparent via-white/30 to-transparent"></div>
    </header>
  );
};

export default Navbar;
