const Home = () => {
  return (
    <section id="home" className="min-h-screen bg-black text-white flex flex-col justify-between px-8 py-12 font-sans">
      {/* Navbar */}
      <div className="flex justify-between text-sm uppercase tracking-wide mb-16">
        <div className="space-x-8">
          <a href="#bio" className="hover:underline">Bio</a>
          <a href="#work" className="hover:underline">Work</a>
        </div>
        <a href="#contact" className="hover:underline">Contact</a>
      </div>

      {/* Main content */}
      <div className="flex flex-col md:flex-row justify-between gap-8">
        {/* Left - Main Heading */}
        <div className="text-4xl md:text-6xl font-bold leading-tight">
          Scaling <span className="italic">Start-ups</span><br />
          for Growth<span className="text-pink-400">.</span>
        </div>

        {/* Center - Quick Links */}
        <div className="text-right flex flex-col gap-2">
          <p className="text-sm uppercase font-semibold text-gray-400">/Quick links</p>
          <div className="flex flex-wrap gap-2 justify-end">
            {['Bio', 'Work', 'Projects', 'Services'].map(item => (
              <a 
                key={item}
                href={`#${item.toLowerCase()}`}
                className="bg-pink-400 text-black font-semibold text-xs py-1.5 px-4 rounded-full hover:bg-pink-500 transition"
              >
                {item}
              </a>
            ))}
          </div>
        </div>

        {/* Right - Contact */}
        <div className="text-sm space-y-2 text-right">
          <p className="text-gray-400 uppercase font-semibold">/Contact</p>
          <p>mykhailo@creativestudio.com</p>
          <p>+44 (857) 857-9984</p>
          <p>1901 Thornridge<br />Shiloh, London<br />81063</p>
        </div>
      </div>

      {/* Footer */}
      <div className="flex justify-between text-xs text-gray-500 mt-16">
        <span>Â©2025 Creative Studio.</span>
        <div className="space-x-6">
          <a href="#">Terms and Conditions</a>
          <a href="#">Privacy Policy</a>
        </div>
      </div>
    </section>
  );
};

export default Home;
