const Home = () => {
  return (
    <section
      id="home"
      className="min-h-screen flex flex-col justify-center items-center text-white px-8"
      style={{
        fontFamily: "'Orbitron', 'sans-serif'",
        textShadow: '0 0 15px rgba(255,255,255,0.1)',
      }}
    >
      {/* Main Heading */}
      <h1 className="text-5xl md:text-7xl font-bold text-center mb-6 leading-tight tracking-tight">
        <span className="text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 via-purple-500 to-pink-500">
          Welcome to My Universe
        </span>
      </h1>

      {/* Subheading */}
      <p className="text-lg md:text-xl text-gray-300 max-w-2xl text-center mb-10">
        Exploring code, creativity, and cosmic inspiration. Dive into my world of projects, ideas, and skills.
      </p>

      {/* CTA Buttons */}
      <div className="flex gap-6">
        <a
          href="#projects"
          className="px-6 py-3 bg-white/10 border border-white/20 rounded-xl backdrop-blur-sm hover:scale-105 transition text-sm tracking-wide"
        >
          View Projects
        </a>
        <a
          href="#contact"
          className="px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-black font-semibold rounded-xl hover:scale-105 transition text-sm tracking-wide"
        >
          Contact Me
        </a>
      </div>
    </section>
  );
};

export default Home;
