import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);


  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header 
      className={`fixed top-0 left-0 w-full z-50 transition-all duration-500 overflow-hidden ${
        scrolled 
          ? 'backdrop-blur-xl border-b border-white/10' 
          : 'backdrop-blur-lg'
      }`}
      style={{
        background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.05) 0%, rgba(147, 51, 234, 0.05) 25%, rgba(6, 182, 212, 0.05) 50%, rgba(168, 85, 247, 0.05) 75%, rgba(59, 130, 246, 0.05) 100%)',
        backgroundSize: '400% 400%',
        animation: 'gradientFlow 15s ease infinite',
        boxShadow: scrolled 
          ? '0 8px 32px rgba(59, 130, 246, 0.15), 0 0 80px rgba(147, 51, 234, 0.1)' 
          : '0 4px 20px rgba(59, 130, 246, 0.1), 0 0 40px rgba(147, 51, 234, 0.05)'
      }}
    >
      {/* Animated background gradient */}
      <div 
        className="absolute inset-0 opacity-30"
        style={{
          background: 'linear-gradient(45deg, transparent 30%, rgba(59, 130, 246, 0.1) 50%, transparent 70%)',
          backgroundSize: '200% 200%',
          animation: 'shimmer 8s ease-in-out infinite'
        }}
      ></div>

      <nav className="relative z-10 w-full px-8 py-4 flex justify-center">
        {/* Navigation Links - Centered and Spread */}
        <ul className="flex space-x-12 font-medium text-base justify-center w-full max-w-4xl" style={{ fontFamily: 'Orbitron, monospace' }}>
          {['Home', 'About', 'Projects', 'Skills', 'Contact'].map((item, index) => (
            <li key={item} className="relative group flex-1 text-center">
              <a 
                href={`#${item.toLowerCase()}`}
                className="relative z-10 inline-block text-white/90 transition-all duration-500 py-3 px-6 rounded-xl border border-transparent hover:border-white/30"
                style={{
                  textShadow: '0 0 15px rgba(255, 255, 255, 0.4)'
                }}
              >
                {item}
              </a>

              {/* Gradient hover background */}
              <div 
                className="absolute inset-0 rounded-xl opacity-0 group-hover:opacity-100 transition-all duration-500 blur-sm"
                style={{
                  background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.3) 0%, rgba(147, 51, 234, 0.3) 25%, rgba(6, 182, 212, 0.3) 50%, rgba(168, 85, 247, 0.3) 75%, rgba(236, 72, 153, 0.3) 100%)',
                  backgroundSize: '300% 300%',
                  animation: 'gradientShift 3s ease infinite'
                }}
              ></div>

              {/* Sharp gradient hover background */}
              <div 
                className="absolute inset-0 rounded-xl opacity-0 group-hover:opacity-70 transition-all duration-500"
                style={{
                  background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.15) 0%, rgba(147, 51, 234, 0.15) 25%, rgba(6, 182, 212, 0.15) 50%, rgba(168, 85, 247, 0.15) 75%, rgba(236, 72, 153, 0.15) 100%)',
                  backgroundSize: '200% 200%',
                  animation: 'gradientHover 2s ease infinite'
                }}
              ></div>

              {/* Animated underline */}
              <div className="absolute bottom-1 left-1/2 w-0 h-1 bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 group-hover:w-3/4 group-hover:left-1/8 transition-all duration-500 rounded-full"></div>

              {/* Glow effect on hover */}
              <div className="absolute inset-0 rounded-xl opacity-0 group-hover:opacity-100 transition-opacity duration-500 bg-gradient-to-r from-blue-500/10 via-purple-500/10 to-pink-500/10 blur-xl"></div>
            </li>
          ))}
        </ul>
      </nav>

      {/* Subtle top border glow */}
      <div className="absolute top-0 left-0 w-full h-px bg-gradient-to-r from-transparent via-blue-400/50 via-purple-400/50 via-cyan-400/50 to-transparent"></div>

      {/* CSS Animations */}
      <style jsx>{`
        @keyframes gradientFlow {
          0%, 100% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
        }

        @keyframes shimmer {
          0%, 100% { transform: translateX(-100%); }
          50% { transform: translateX(100%); }
        }

        @keyframes gradientShift {
          0%, 100% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
        }

        @keyframes gradientHover {
          0%, 100% { background-position: 0% 0%; }
          50% { background-position: 100% 100%; }
        }
      `}</style>
    </header>
  );
};

export default Navbar;
