import { motion } from 'framer-motion';

const projects = [
  {
    title: 'Portfolio Website',
    description: 'Built using React, TailwindCSS and animations.',
    link: 'https://yourportfolio.com'
  },
  {
    title: 'Mini Payment Gateway',
    description: 'Backend simulation of Razorpay using Node/Express.',
    link: 'https://github.com/your/project'
  }
];

const Projects = () => {
  return (
    <div className="text-white">
      <h1 className="text-4xl font-bold text-center mb-6">My Projects</h1>
      <div className="grid md:grid-cols-2 gap-6">
        {projects.map((project, index) => (
          <motion.div
            key={index}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.97 }}
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.2 }}
            className="bg-glass backdrop-blur-md p-6 rounded-xl shadow-lg border border-white/10"
          >
            <h2 className="text-2xl font-semibold mb-2">{project.title}</h2>
            <p className="text-sm mb-4">{project.description}</p>
            <a
              href={project.link}
              className="text-blue-300 hover:underline text-sm"
              target="_blank"
              rel="noopener noreferrer"
            >
              View Project â†’
            </a>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Projects;
