import { useState, useEffect } from 'react';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header
      className={`fixed top-0 left-0 w-full z-50 transition-all duration-500 overflow-hidden ${
        scrolled ? 'backdrop-blur-xl border-b border-white/10' : 'backdrop-blur-lg'
      }`}
      style={{
        background: 'linear-gradient(135deg, rgba(17, 24, 39, 0.8) 0%, rgba(31, 41, 55, 0.8) 100%)',
        backdropFilter: 'blur(20px)',
        WebkitBackdropFilter: 'blur(20px)',
      }}
    >
      <nav className="relative z-10 w-full px-8 py-4 flex justify-center">
        <ul className="flex space-x-12 font-medium text-base justify-center w-full max-w-4xl">
          {['Home', 'About', 'Projects', 'Skills', 'Contact'].map((item) => (
            <li key={item} className="relative group flex-1 text-center">
              <a
                href={`#${item.toLowerCase()}`}
                className="relative z-10 inline-block text-transparent bg-clip-text bg-gradient-to-r from-purple-400 via-pink-500 to-blue-500 transition-all duration-500 py-3 px-6 rounded-xl hover:scale-105"
                style={{
                  fontFamily: 'Orbitron, monospace',
                }}
              >
                {item}
              </a>
              {/* Gradient glow hover */}
              <div
                className="absolute inset-0 rounded-xl opacity-0 group-hover:opacity-100 transition-all duration-500 blur-md"
                style={{
                  background:
                    'linear-gradient(135deg, rgba(168, 85, 247, 0.15), rgba(236, 72, 153, 0.15), rgba(59, 130, 246, 0.15))',
                  backgroundSize: '300% 300%',
                }}
              ></div>
              {/* Animated underline */}
              <div className="absolute bottom-1 left-1/2 w-0 h-1 bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 group-hover:w-3/4 group-hover:left-1/8 transition-all duration-500 rounded-full"></div>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
};

export default Navbar;
